import React from 'react';
import './styles.css';
import { ShoppingList, ShoppingListCollection } from './ShoppingList'

export class ShoppingListSelection extends React.Component {
    state = {
        seen: false
        };
       togglePop = () => {
        this.setState({
         seen: !this.state.seen
        });
       };

        constructor(props) {
            super(props);
            this.state = {
            value: '',
            shoppingList: shoppingList,
            };
            this.handleChange = this.handleChange.bind(this);
            this.handleAddList = this.handleAddList.bind(this);
      }

       handleAddList = () => {
        alert('new list name: ' + this.state.value);

       }

       handleChange(event) {
        this.setState({value: event.target.value});
      }


    render() {
        return(
            <section className="">
                <h1>Shopping Lists</h1>
                <button onClick={this.togglePop}>+ Create New List</button>
                {this.state.seen ?    
                    <div className="modal">
                        <div className="modal_content">
                            <span className="close">&times;    </span>
                                <form onSubmit={this.handleAddList}>
                                    <input type="text" placeholder="New Shopping List Name" onChange={this.handleChange}/>
                                <br />
                                <input type="submit" value="Add" />
                                </form>
                        </div>
                     </div> : null}

                {
                    ShoppingListCollection.collection.map(listName => (
                        <p><button onClick="">
                                {listName.name}
                            </button></p>


                    ))



                }

            </section>
            
        );
    }
}

export default ShoppingListSelection;
