import React from 'react';
import './styles.css';
import { ShoppingList, ShoppingListCollection } from './ShoppingList'

export class ShoppingListSelection extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            value: '',
            lists: ShoppingListCollection.collection,
        }
        this.handleInput = this.handleInput.bind(this);
        this.handleAddList = this.handleAddList.bind(this);
    }

    state = {
        seen: false
        };
       togglePop = () => {
        this.setState({
         seen: !this.state.seen
        });
    };

    handleAddList = (name) => {
        alert('new list name: ' + name);
        alert('current lists: ' + this.state.lists.toString())

        //this.state.lists.add(this.state.value, []);
       //this.state.lists.add(name, []);
       
        //this.setState({lists: temp})


        var temp = [...this.state.lists];
        temp.push(name, []);
        //alert('size of collection ' + temp.length);

        this.setState(() => {
            return {
                ...this.state.lists,
                lists: temp
            }
        });
        alert('temp lists: ' + temp.toString())
        

        //this.forceUpdate();
    }

    handleInput(event) {
        this.setState({value: event.target.value});
    }


    render() {
        return(

            <section className="">
                <h1>Shopping Lists</h1>
                <button onClick={this.togglePop}>+ Create New List</button>
                {this.state.seen ?    
                    <div className="modal">
                        <div className="modal_content">
                            <span className="close"></span>
                                <form onSubmit={this.handleAddList.bind(this, this.state.value)}>
                                    <input type="text" placeholder="New Shopping List Name" onChange={this.handleInput}/>
                                <br />
                                <input type="submit" value="Add" />
                                </form>
                        </div>
                     </div> : null}

                {
      
                    this.state.lists.map((listName, index) => (

                        <p><button onClick={this.props.changeListHandler.bind(this, index)}>
                                {listName.name}
                            </button></p>


                    ))



                }
                {
                    this.state.preventDefault()
                }

            </section>
            
        );
    }
}

export default ShoppingListSelection;
