import React from 'react';
import './styles.css';
import { Link } from 'react-router-dom';
import GetMockData from './GetMockData'
import ShoppingList from './ShoppingList';

const select = [
    {index: 0, name: 'Breakfast', food: 'bread', products: ['bread', 'eggs', 'milk']},
    {index: 1, name: 'Dinner', food: 'eggs', products: ['steak', 'ketchup', 'fries']},
    {index: 2, name: 'Snacks', food: 'chips', products: ['chips', 'grapes', 'cookies']},
    {index: 3, name: 'Drinks', food: 'pepsi', products: ['Pepsi', 'Coke', 'orange juice']},
]

var chosenList = -1;

export class ShoppingListViewer extends React.Component {
    render() {
        const showList = (index) => {
            

            chosenList = index;
            alert(`hello, ${chosenList}`);
        }


    function renderSwitch(chosenList) {
                switch(chosenList) {
                    case 0: return <ShoppingList selectedList={0}/>;
                    case 1: return <ShoppingList selectedList={1}/>;
                    case 2: return <ShoppingList selectedList={2}/>;
                    case 3: return <ShoppingList selectedList={3}/>;
                    default: return <ShoppingList selectedList={2}/>
                }
            }

        return(
            <section className='container'>
                <div className='viewer'>
       
                    <div className='listoflists'>
                        <h1>Shopping Lists</h1>
                            <button onClick="">+ Create New List</button>

                            {select.map(listName => (
                                    <p><button onClick={() => showList(listName.index)} >
                                        <GetMockData
                                            key={listName.name}
                                            name={listName.name}
                                            
                                        />
                                    </button></p>
                            ))}
                    </div>

                    <div className='listoflists'>

                        


{items && items.map(item => {renderSwitch(chosenList)})}

          
                    </div>

                        


                
                </div>
            </section>
        );
    }

}



export default ShoppingListViewer;
