import React, { Component } from "react";
import { shoppingList, ListObject } from './mockdata'


export default class PopUp extends Component {
  handleClick = () => {
   this.props.toggle();
  };

  handleAddList = () => {
    const temp = this.state.shoppingList.slice(0);

    temp.push(new ListObject(shoppingList.length-1,  'new list', 'pancake mix', ['whisk', 'strawberries', 'chocolate chips']));
    this.setState({
        shoppingList: temp,
    });

}

render() {
  return (
   <div className="modal">
     <div className="modal_content">
     <span className="close" onClick={this.handleClick}>&times;    </span>
     <form>
         <input type="text" placeholder="New Shopping List Name"/>

     <br />
     <input type="submit"/>

     </form>
    </div>
   </div>
  );
 }
}
