import React from 'react';
import './styles.css';
import { Link } from 'react-router-dom';
import { shoppingList, ListObject } from './mockdata'
import InputPopUp from './InputPopUp'
import GetMockData from './GetMockData'



export class ListOfLists extends React.Component{
    state = {
        seen: false
        };
       togglePop = () => {
        this.setState({
         seen: !this.state.seen
        });
       };

    render() {
        const showList = (index) => {
            

            this.props.chosenList = index;
            //alert(`hello, ${chosenList}`);
            this.forceUpdate();
        }

        return(
            <section className="listoflists">
                <h1>Shopping Lists</h1>
                <button onClick={this.togglePop}>+ Create New List</button>
                {this.state.seen ? <InputPopUp toggle={this.togglePop} /> : null}
                
                {shoppingList.map(listName => (
                        <p><button onClick={() => showList(listName.index)} >
                            <GetMockData
                                key={listName.name}
                                name={listName.name}
                            />
                        </button></p>
                ))}

                </section>
        );
    }

}
export default ListOfLists;
