import React from "react";
import ReactDOM from "react-dom";
import "./styles.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Dropdown from "react-bootstrap/Dropdown";

//https://stackoverflow.com/questions/60123638/use-three-dots-span-to-trigger-dropdown/60127027#60127027

const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
  <a
    href=""
    ref={ref}
    onClick={e => {
      e.preventDefault();
      onClick(e);
    }}
  >
    {children}
    <span className="threedots" />
  </a>
));
export default function App() {
  return (
    <div className="App">
      <h1>Custom toggle Icon</h1>
      <Dropdown>
        <Dropdown.Toggle as={CustomToggle} />
        <Dropdown.Menu size="sm" title="">
          <Dropdown.Header>Options</Dropdown.Header>
          <Dropdown.Item>abcd</Dropdown.Item>
          <Dropdown.Item>erty</Dropdown.Item>
          <Dropdown.Item>hnjm</Dropdown.Item>
        </Dropdown.Menu>
      </Dropdown>
    </div>
  );
}
