import React, { Component } from "react";
import { shoppingList, ListObject } from './mockdata'

//const [setLists] = useState([]);

export default class PopUp extends Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};
    this.handleChange = this.handleChange.bind(this);
    this.handleAddList = this.handleAddList.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleClick = () => {
   this.props.toggle();
  };

  handleAddList = () => {
    alert('you clicked submit! new list name: ' + this.state.value);
    shoppingList([...shoppingList, new ListObject(shoppingList.length-1,  this.state.value, '', [])])

    //const temp = this.state.shoppingList.slice(0);

    //temp.push(new ListObject(shoppingList.length-1,  'new list', '', []));
    
    //this.setState(temp);
}

render() {
  return (
   <div className="modal">
     <div className="modal_content">
     <span className="close" onClick={this.handleClick}>&times;    </span>
     <form onSubmit={this.handleAddList}>
         <input type="text" placeholder="New Shopping List Name" value={this.state.value} onChange={this.handleChange}/>

     <br />
     <input type="submit" value="Submit" />

     </form>
    </div>
   </div>
  );
 }
}
