import React, { Component, useState } from "react";
import { shoppingList, ListObject } from './mockdata'

const [newName] = useState("");

export default class InputPopUp extends Component {
  handleClick = () => {
   this.props.toggle();
  };

  handleChange = (event) => {
    event.preventDefault();
    alert(`The name you entered was: ${newName}`);
      //shoppingList.push(2, newName, '', []);
      
  }

  
render() {
  return (
   <div className="modal">
     <div className="modal_content">
     <span className="close" onClick={this.handleClick}>&times;    </span>
     <form onSubmit={handleChange}>
         <input type="text" value={newName} placeholder="New Shopping List Name" onChange={(e) => setName(e.target.value)}/>

     <br />
     <input type="submit"/>

     </form>
    </div>
   </div>
  );
 }
}
